import{_ as i,c as e,o as a,aj as o}from"./chunks/framework.BZD6ashX.js";const h=JSON.parse('{"title":"🪟Linux 命令基础","description":"","frontmatter":{},"headers":[],"relativePath":"Linux命令.md","filePath":"Linux命令.md"}'),p={name:"Linux命令.md"};function d(c,l,r,t,u,n){return a(),e("div",null,[...l[0]||(l[0]=[o('<h1 id="🪟linux-命令基础" tabindex="-1">🪟Linux 命令基础 <a class="header-anchor" href="#🪟linux-命令基础" aria-label="Permalink to “🪟Linux 命令基础”">​</a></h1><p>以下是按功能分类的 Linux 常用命令总结，适合快速查找和复习：</p><h3 id="_1-文件与目录管理-最基础" tabindex="-1">1. 文件与目录管理 (最基础) <a class="header-anchor" href="#_1-文件与目录管理-最基础" aria-label="Permalink to “1. 文件与目录管理 (最基础)”">​</a></h3><ul><li><p><code>ls</code>：列出目录内容。</p><ul><li><p>ls -l：显示详细信息（权限、大小、时间）。</p></li><li><p>ls -a：显示所有文件（包括以.开头的隐藏文件）。</p></li></ul></li><li><p><code>cd</code>：切换当前目录 (Change Directory)。</p><ul><li><p>cd /path/to/dir：进入指定目录。</p></li><li><p>cd ..：返回上一级目录。</p></li><li><p>cd ~：回到用户主目录。</p></li></ul></li><li><p><code>pwd</code>：显示当前工作目录的绝对路径。</p></li><li><p><code>mkdir</code>：创建新目录。</p><ul><li>mkdir -p a/b/c：递归创建多级目录。</li></ul></li><li><p><code>touch</code>：创建空文件或更新文件时间戳。</p></li><li><p><code>cp</code>：复制文件或目录。</p><ul><li><p>cp file1 file2：复制文件。</p></li><li><p>cp -r dir1 dir2：递归复制目录。</p></li></ul></li><li><p><code>mv</code>：移动文件或重命名。</p></li><li><p><code>rm</code>：删除文件或目录。</p><ul><li><p>rm file：删除文件。</p></li><li><p>rm -rf dir：强制递归删除目录（<strong>慎用！</strong>）。</p></li></ul></li><li><p><code>find</code>：查找文件。</p><ul><li>find / -name &quot;filename&quot;：从根目录按名称查找文件。</li></ul></li></ul><h3 id="_2-文件查看与内容处理" tabindex="-1">2. 文件查看与内容处理 <a class="header-anchor" href="#_2-文件查看与内容处理" aria-label="Permalink to “2. 文件查看与内容处理”">​</a></h3><ul><li><p><code>cat</code>：查看文件全部内容。</p></li><li><p><code>more / less</code>：分页查看文件内容（less  支持上下翻页，更常用）。</p></li><li><p><code>head</code>：查看文件开头几行。</p><ul><li>head -n 10 file.txt：看前 10 行。</li></ul></li><li><p><code>tail</code>：查看文件结尾几行。</p><ul><li>tail -f log.txt：<strong>实时</strong>滚动查看日志文件（非常常用）。</li></ul></li><li><p><code>grep</code>：文本搜索工具（强大）。</p><ul><li><p>grep &quot;error&quot; file.txt：在文件中搜索字符串。</p></li><li><p>grep -r &quot;string&quot; .：在当前目录递归搜索。</p></li></ul></li><li><p><code>vim / nano</code>：命令行文本编辑器。</p><ul><li>vim：功能强大，需学习操作模式（i 插入，:wq 保存退出）。</li></ul></li></ul><h3 id="_3-系统信息与进程管理" tabindex="-1">3. 系统信息与进程管理 <a class="header-anchor" href="#_3-系统信息与进程管理" aria-label="Permalink to “3. 系统信息与进程管理”">​</a></h3><ul><li><p><code>top / htop</code>：实时显示系统资源使用情况（CPU、内存、进程）。</p></li><li><p><code>ps</code>：查看当前进程快照。</p><ul><li>ps -ef  或  ps aux：显示所有进程详细信息。</li></ul></li><li><p><code>kill</code>：终止进程。</p></li><li><p><code>df</code>：查看磁盘空间使用情况。</p><ul><li>df -h：以易读格式（GB, MB）显示。</li></ul></li><li><p><code>free</code>：查看内存使用情况。</p><ul><li>free -h：以易读格式显示。</li></ul></li><li><p><code>uname</code>：查看系统内核信息。</p><ul><li>uname -a：显示所有系统信息。</li></ul></li></ul><h3 id="_4-权限与用户管理" tabindex="-1">4. 权限与用户管理 <a class="header-anchor" href="#_4-权限与用户管理" aria-label="Permalink to “4. 权限与用户管理”">​</a></h3><ul><li><p><code>chmod</code>：修改文件权限。</p><ul><li><p>chmod 755 file：赋予所有者读写执行权限，其他人读执行权限。</p></li><li><p>chmod +x script.sh：赋予脚本执行权限。</p></li></ul></li><li><p><code>chown</code>：修改文件所有者。</p><ul><li>chown user:group file：修改文件的用户和组。</li></ul></li><li><p><code>sudo</code>：以超级管理员（root）身份执行命令。</p></li><li><p><code>su</code>：切换用户。</p><ul><li><code>su</code> -：切换到 root 用户并加载环境变量。</li></ul></li><li><p><code>useradd / userdel</code>：添加/删除用户。</p></li><li><p><code>passwd</code>：修改用户密码。</p></li></ul><h3 id="_5-网络管理" tabindex="-1">5. 网络管理 <a class="header-anchor" href="#_5-网络管理" aria-label="Permalink to “5. 网络管理”">​</a></h3><ul><li><p><code>ping</code>：测试网络连通性。</p></li><li><p><code>ip</code>：查看和配置网络接口（取代了老的  ifconfig）。</p><ul><li><code>ip addr</code>：查看 IP 地址。</li></ul></li><li><p><code>netstat / ss</code>：查看网络端口和连接。</p><ul><li><code>netstat -tulnp</code>：查看所有监听端口及对应进程。</li></ul></li><li><p><code>ssh</code>：远程登录。</p><ul><li><code>ssh user@ip_address</code>。</li></ul></li><li><p><code>curl / wget</code>：文件下载或发送网络请求。</p><ul><li><code>curl http://example.com</code>：获取网页内容。</li></ul></li></ul><h3 id="_6-压缩与解压" tabindex="-1">6. 压缩与解压 <a class="header-anchor" href="#_6-压缩与解压" aria-label="Permalink to “6. 压缩与解压”">​</a></h3><ul><li><p><code>tar</code>：打包和解压工具（最常用）。</p><ul><li><p><code>tar -czvf archive.tar.gz dir/</code>：打包并压缩为 .tar.gz。</p></li><li><p><code>tar -xzvf archive.tar.gz</code>：解压 .tar.gz。</p></li></ul></li><li><p><code>zip / unzip</code>：处理 .zip 格式。</p></li></ul><h3 id="_7-软件安装-根据发行版不同" tabindex="-1">7. 软件安装 (根据发行版不同) <a class="header-anchor" href="#_7-软件安装-根据发行版不同" aria-label="Permalink to “7. 软件安装 (根据发行版不同)”">​</a></h3><ul><li><p><strong>Ubuntu/Debian 系列</strong>:</p><ul><li><p>apt update：更新软件源列表。</p></li><li><p>apt install package_name：安装软件。</p></li></ul></li><li><p><strong>CentOS/RHEL 系列</strong>:</p><ul><li>yum install package_name (旧版) 或  dnf install package_name (新版)。</li></ul></li></ul><h3 id="_8-实用快捷键与技巧" tabindex="-1">8. 实用快捷键与技巧 <a class="header-anchor" href="#_8-实用快捷键与技巧" aria-label="Permalink to “8. 实用快捷键与技巧”">​</a></h3><ul><li><p><strong>Tab 键</strong>：自动补全命令或文件名（<strong>必用技巧</strong>）。</p></li><li><p><strong>Ctrl + C</strong>：终止当前正在运行的命令。</p></li><li><p><strong>Ctrl + L</strong>：清屏（相当于  clear  命令）。</p></li><li><p><strong>| (管道符)</strong>：将前一个命令的输出作为后一个命令的输入。</p><ul><li>例如：ps -ef | grep java (查找包含 java 的进程)。</li></ul></li></ul>',18)])])}const _=i(p,[["render",d]]);export{h as __pageData,_ as default};
